function showMyImage(e){for(var t=e.files,a=0;a<t.length;a++){var n=t[a];if(n.type.match(/image.*/)){var r=e.parentNode;r.file=n;var o=new FileReader;o.onload=function(e){r.style.backgroundImage=`url(${e.target.result})`},o.readAsDataURL(n)}}}document.addEventListener("keypress",function(e){if("Enter"!==e.key)return;let t=e.target;if(t.classList.contains("focusNext")){var a=document.querySelector('[tabindex="'+(t.tabIndex+1)+'"]');a&&(a.focus(),event.preventDefault())}});